package com.aigilas;import com.spx.io.Client;import com.spx.io.Server;import com.xna.wrapper.Console;import com.xna.wrapper.Environment;import com.xna.wrapper.Game;    public class EntryPoint    {        public static void main(String[] args)        {            Thread server = new Thread(new ServerThread());            server.run();            try {				Thread.sleep(100);			} catch (InterruptedException e) {								e.printStackTrace();			}            Game game = new Game();            game.Initialize();            while(game.IsRunning())            {            	game.Update();                          }            Server.Get().Close();            Client.Get().Close();            Console.WriteLine("Finished being a client");            Environment.Exit();        }    }