package com.aigilas.creatures;import com.aigilas.entities.Elements;import com.aigilas.management.SpriteType;import com.aigilas.strategies.Strategy;import com.aigilas.strategies.StrategyFactory;public     class Hand extends  AbstractCreature    {        public Hand(){ super(AigilasActorType.HAND,SpriteType.HAND);            Compose(Elements.PHYSICAL);            Strengths(StatType.STRENGTH, StatType.STRENGTH);            _isBlocking = false;            _strategy = StrategyFactory.Create(Strategy.StraightLine, this);        }    }