package com.aigilas.entities;import java.util.List;import com.aigilas.creatures.Player;import com.aigilas.gods.God;import com.aigilas.items.GenericItem;import com.aigilas.management.SpriteType;import com.spx.core.Point2;import com.spx.entities.Entity;import com.spx.entities.EntityManager;import com.spx.entities.IEntity;import com.spx.text.ActionText;import com.spx.text.TextManager;public class Altar extends Entity {	private God _god;	private Player _currentTarget;	private List<IEntity> _offerings;	public Altar(Point2 location, int godName) {		_god = God.Get(godName);		_graphic.SetColor(_god.GetColor());		Initialize(location, SpriteType.ALTAR, com.aigilas.EntityType.ALTAR,				com.aigilas.Depth.Altar);	}	@Override	public void Update() {		_currentTarget = (Player) EntityManager.GetTouchingCreature(this);		if (_currentTarget != null) {			if (_currentTarget.IsInteracting()) {				_currentTarget.Pray(_god);			}			_offerings = EntityManager.GetEntities(com.aigilas.EntityType.ITEM,					_location);			for (IEntity offering : _offerings) {				_currentTarget.Sacrifice(_god, (GenericItem) offering);			}			TextManager.Add(new ActionText(_god.NameText, 1,					(int) GetLocation().PosX, (int) GetLocation().PosY));		}	}	public God GetGod() {		return _god;	}}