package com.aigilas.entities;import com.aigilas.creatures.ICreature;import com.aigilas.dungeons.DungeonFactory;import com.aigilas.management.SpriteType;import com.spx.core.Point2;import com.spx.entities.Entity;import com.spx.entities.EntityManager;public class Downstairs extends Entity {	public Downstairs(Point2 location) {		Initialize(location, SpriteType.DOWNSTAIRS,				com.aigilas.EntityType.DOWNSTAIRS, com.aigilas.Depth.Stairs);	}	private ICreature player;	@Override	public void Update() {		player = (ICreature) EntityManager.GetTouchingCreature(this);		if (player != null) {			if (player.IsInteracting()) {				player.PerformInteraction();				try {					DungeonFactory.GetNextFloor();				} catch (Exception e) {					e.printStackTrace();				}			}		}	}}