package com.aigilas.entities;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.Player;import com.aigilas.dungeons.DungeonFactory;import com.aigilas.management.SpriteType;import com.spx.core.Point2;import com.spx.entities.Entity;import com.spx.entities.EntityManager;    public class Upstairs  extends  Entity    {        public Upstairs(Point2 location)        {            Initialize(location, SpriteType.UPSTAIRS, com.aigilas.EntityType.UPSTAIRS,com.aigilas.Depth.Stairs);        }        ICreature player;@Override        public  void  Update()        {            player = (Player)EntityManager.GetTouchingCreature(this);            if(player!=null)            {                if (player.IsInteracting())                {                    player.PerformInteraction();                    try {						DungeonFactory.GetPreviousFloor();					} catch (Exception e) {						e.printStackTrace();					}                }                }        }    }