package com.aigilas.hud;import com.aigilas.creatures.ICreature;import com.aigilas.items.Equipment;import com.aigilas.items.Inventory;    public class HudManager    {        private InventoryHud _inventory;        private SkillHud _skill;        public HudManager(ICreature parent,Inventory inventory,Equipment equipment)        {            _inventory = new InventoryHud(parent, inventory, equipment);            _skill = new SkillHud(parent);            _skill.Toggle();        }        public boolean ToggleInventory()        {            _inventory.Toggle();            _skill.Toggle();            return _inventory.IsVisible();        }        public void Update()        {            _inventory.Update();            _skill.Update();        }        public void Draw()        {            _inventory.Draw();            _skill.Draw();        }    }