package com.aigilas.reactions.impl;import com.aigilas.creatures.ICreature;import com.aigilas.reactions.IReaction;import com.spx.entities.EntityManager;import com.spx.entities.IActor;    public class ExplosionReaction  implements IReaction    {        @Override		public void Affect(ICreature target)        {            for (IActor creature:EntityManager.GetActorsSurrounding(target.GetLocation(), 2))            {                ((ICreature)creature).ApplyDamage(10);            }        }    }