package com.aigilas.skills.animations;import com.aigilas.creatures.ICreature;import com.aigilas.entities.SkillEffect;import com.spx.core.Point2;public     class RangedAnimation  extends  SkillAnimation    {@Override        public  void Animate(SkillEffect skill, ICreature source, Point2 velocity)        {            if (!skill.Move(velocity.X, velocity.Y))            {                skill.Cleanup(skill);            }        }    }