package com.aigilas.skills.behaviors;import com.aigilas.creatures.ICreature;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;public class RangedBehavior extends SkillBehavior {	public RangedBehavior(int effectGraphic, ISkill parentSkill) {		super(effectGraphic, AnimationType.RANGED, parentSkill);	}	@Override	public void Activate(ICreature target) {		if (SubtractCost(target)) {			_sideEffects.Generate(					target.GetLocation().Add(target.GetSkillVector()),					target.GetSkillVector(), target);		}	}}