package com.aigilas.skills.impl;import com.aigilas.statuses.StatusFactory;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.entities.Elements;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;    public class AbsorbSkill  extends  ISkill    {        public AbsorbSkill()            { super(SkillId.ABSORB, AnimationType.RANGED);            Add(Elements.LIGHT);            AddCost(StatType.MANA, 10);        }@Override        public  void Affect(ICreature target)        {            target.ApplyDamage(10, _source);            _source.ApplyDamage(-10);            target.ApplyDamage(10, _source, true, StatType.MANA);            _source.ApplyDamage(-10, _source, true, StatType.MANA);        }    }