package com.aigilas.skills.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.entities.*;import com.aigilas.skills.*;import com.spx.sprites.*;import com.aigilas.statuses.*;import com.aigilas.items.*;import com.spx.entities.*;import com.spx.core.*;    public class AbsorbSkill  extends  ISkill    {        public AbsorbSkill()            { super(SkillId.ABSORB, AnimationType.RANGED);            Add(Elements.LIGHT);            AddCost(StatType.MANA, 10);        }@Override        public  void Affect(ICreature target)        {            target.ApplyDamage(10, _source);            _source.ApplyDamage(-10);            target.ApplyDamage(10, _source, true, StatType.MANA);            _source.ApplyDamage(-10, _source, true, StatType.MANA);        }    }