package com.aigilas.skills.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.entities.*;import com.aigilas.skills.*;import com.spx.sprites.*;import com.aigilas.statuses.*;import com.aigilas.items.*;import com.spx.entities.*;import com.spx.core.*;    public class BrimstoneSkill  extends  ISkill    {        private Point2 _direction = new Point2(0, 0);        public BrimstoneSkill()            { super(SkillId.ACID_NOZZLE, AnimationType.STATIONARY);            AddCost(StatType.MANA, 10);        }@Override        public  void Activate(ICreature source)        {            for (int ii = -1; ii < 2; ii++)            {                for (int jj = -1; jj < 2; jj++)                {                    if (ii != 0 || jj != 0)                    {                        _direction.Reset(ii, jj);                        source.SetSkillVector(_direction);                        SkillFactory.Create(SkillId.FIREBALL).Activate(source);                    }                }            }        }    }