package com.aigilas.skills.impl;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;import com.spx.core.Point2;    public class BrimstoneSkill  extends  ISkill    {        private Point2 _direction = new Point2(0, 0);        public BrimstoneSkill()            { super(SkillId.ACID_NOZZLE, AnimationType.STATIONARY);            AddCost(StatType.MANA, 10);        }@Override        public  void Activate(ICreature source)        {            for (int ii = -1; ii < 2; ii++)            {                for (int jj = -1; jj < 2; jj++)                {                    if (ii != 0 || jj != 0)                    {                        _direction.Reset(ii, jj);                        source.SetSkillVector(_direction);                        SkillFactory.Create(SkillId.FIREBALL).Activate(source);                    }                }            }        }    }