package com.aigilas.skills.impl;import com.aigilas.statuses.StatusFactory;import com.aigilas.skills.*;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.entities.*;import com.spx.sprites.*;import com.aigilas.statuses.*;import com.aigilas.items.*;import com.spx.entities.*;import com.spx.core.*;    public class CombustSkill  extends  ISkill    {        private static final int CombustDistance = 1;        public CombustSkill()            { super(SkillId.COMBUST, AnimationType.RANGED);            Add(Elements.AIR, Elements.PHYSICAL);            AddCost(StatType.MANA, 10);        }@Override        public  void Affect(ICreature target)        {            target.ApplyDamage(10, _source);            if (!target.IsActive())            {                List<IActor> targets = EntityManager.GetActorsSurrounding(target.GetLocation(), CombustDistance);                for (int ii = 0; ii < targets.size(); ii++)                {                    StatusFactory.Apply((ICreature)targets.get(ii), Status.Burn);                }            }        }    }