package com.aigilas.skills.impl;import java.util.List;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.entities.Elements;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;import com.aigilas.statuses.Status;import com.aigilas.statuses.StatusFactory;import com.spx.entities.EntityManager;import com.spx.entities.IActor;    public class CombustSkill  extends  ISkill    {        private static final int CombustDistance = 1;        public CombustSkill()            { super(SkillId.COMBUST, AnimationType.RANGED);            Add(Elements.AIR, Elements.PHYSICAL);            AddCost(StatType.MANA, 10);        }@Override        public  void Affect(ICreature target)        {            target.ApplyDamage(10, _source);            if (!target.IsActive())            {                List<IActor> targets = EntityManager.GetActorsSurrounding(target.GetLocation(), CombustDistance);                for (int ii = 0; ii < targets.size(); ii++)                {                    StatusFactory.Apply((ICreature)targets.get(ii), Status.Burn);                }            }        }    }