package com.aigilas.skills.impl;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.entities.Elements;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;import com.aigilas.statuses.Status;    public class DartSkill  extends  ISkill    {        public DartSkill()            { super(SkillId.DART, AnimationType.RANGED);            AddCost(StatType.MANA, 10);            Add(Elements.DARK);        }@Override        public  void Affect(ICreature target)        {            StatusFactory.Apply(target, Status.Poison);            target.ApplyDamage(5, _source);        }    }