package com.aigilas.skills.impl;import com.aigilas.statuses.StatusFactory;import com.aigilas.creatures.AigilasActorType;import com.aigilas.creatures.CreatureFactory;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.entities.Elements;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;import com.spx.core.GameManager;import com.spx.core.Point2;import com.spx.core.RNG;    public class DismembermentSkill  extends  ISkill    {        public DismembermentSkill()            { super(SkillId.DISMEMBERMENT, AnimationType.SELF);            Add(Elements.PHYSICAL);            AddCost(StatType.MANA, 3);        }@Override        public  void Activate(ICreature target)        {            super.Activate(target);            int openCell = RNG.Next(1, GameManager.TileMapWidth - 1);            for (int ii = 1; ii < GameManager.TileMapWidth - 1; ii++)            {                if (ii != openCell)                {                    CreatureFactory.Create(AigilasActorType.HAND, new Point2(ii, 1));                }            }        }    }