package com.aigilas.skills.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.entities.*;import com.aigilas.skills.*;import com.spx.sprites.*;import com.aigilas.statuses.*;import com.aigilas.items.*;import com.spx.entities.*;import com.spx.core.*;    public class DismembermentSkill  extends  ISkill    {        public DismembermentSkill()            { super(SkillId.DISMEMBERMENT, AnimationType.SELF);            Add(Elements.PHYSICAL);            AddCost(StatType.MANA, 3);        }@Override        public  void Activate(ICreature target)        {            super.Activate(target);            int openCell = RNG.Next(1, GameManager.TileMapWidth - 1);            for (int ii = 1; ii < GameManager.TileMapWidth - 1; ii++)            {                if (ii != openCell)                {                    CreatureFactory.Create(AigilasActorType.HAND, new Point2(ii, 1));                }            }        }    }