package com.aigilas.skills.impl;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.entities.Elements;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;public class FireballSkill extends ISkill {	public FireballSkill()	{		super(SkillId.FIREBALL, AnimationType.RANGED);		Add(Elements.FIRE);		AddCost(StatType.MANA, 10);	}	@Override	public void Affect(ICreature target)	{		target.ApplyDamage(20, _source);	}}