package com.aigilas.skills.impl;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.entities.Elements;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;public class ForgetSkill extends ISkill{	public ForgetSkill()	{		super(SkillId.FORGET_SKILL, AnimationType.SELF);		Add(Elements.MENTAL);		AddCost(StatType.MANA, 10);	}	@Override	public void Activate(ICreature source) {		super.Activate(source);		_source.RemoveLeastUsedSkill();	}}