package com.aigilas.skills.impl;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.entities.Elements;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;import com.aigilas.statuses.Status;    public class MutinySkill  extends  ISkill    {        public MutinySkill()            { super(SkillId.MUTINY, AnimationType.RANGED);            Add(Elements.MENTAL, Elements.PHYSICAL);            AddCost(StatType.MANA, 10);        }@Override        public  void Affect(ICreature target)        {            StatusFactory.Apply(target, Status.Mutiny);        }    }