package com.aigilas.skills.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.entities.*;import com.aigilas.skills.*;import com.spx.sprites.*;import com.aigilas.statuses.*;import com.aigilas.items.*;import com.spx.entities.*;import com.spx.core.*;    public class RemoteMineSkill  extends  ISkill    {        private static HashMap<ICreature, RemoteMineSkill> _cache = new HashMap<ICreature, RemoteMineSkill>();        public RemoteMineSkill()            { super(SkillId.REMOTE_MINE, AnimationType.STATIONARY);            AddCost(StatType.MANA, 10);            Add(Elements.FIRE);        }@Override        public  void Activate(ICreature source)        {            if (!_cache.containsKey(source))            {                _cache.add(source, this);                super.Activate(source);            }            else            {                if (_cache[source].IsActive())                {                    _cache[source].Explode(source);                    _cache[source].Cleanup(source, null);                    _cache.remove(source);                    Cleanup(source, null);                }                else                {                    _cache.remove(source);                    _cache.add(source, this);                    super.Activate(source);                }            }        }        private void Explode(ICreature source)        {            if (_behavior.GetGraphic() != null)            {                CreatureFactory.CreateMinion(SkillId.EXPLODE, _source, _behavior.GetGraphic(), _behavior.GetGraphic().GetLocation());            }        }    }