package com.aigilas.skills.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.entities.*;import com.aigilas.skills.*;import com.spx.sprites.*;import com.aigilas.statuses.*;import com.aigilas.items.*;import com.spx.entities.*;import com.spx.core.*;    public class SerpentSupperSkill  extends  ISkill    {        public SerpentSupperSkill()            { super(SkillId.SERPENT_SUPPER, AnimationType.SELF);            Add(Elements.MENTAL);            AddCost(StatType.MANA, 10);        }@Override        public  void Activate(ICreature source)        {            for (int ii = 1; ii < GameManager.TileMapWidth - 1; ii++)            {                if (ii != GameManager.TileMapHeight / 2)                {                    CreatureFactory.Create(AigilasActorType.SERPENT, new Point2(ii, GameManager.TileMapHeight / 2));                }            }            for (int ii = 1; ii < GameManager.TileMapHeight - 1; ii++)            {                if (ii != GameManager.TileMapWidth / 2)                {                    CreatureFactory.Create(AigilasActorType.SERPENT, new Point2(GameManager.TileMapWidth / 2, ii));                }            }        }    }