package com.aigilas.skills.impl;import com.aigilas.statuses.StatusFactory;import com.aigilas.creatures.AigilasActorType;import com.aigilas.creatures.CreatureFactory;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.entities.Elements;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;import com.spx.core.GameManager;import com.spx.core.Point2;    public class SerpentSupperSkill  extends  ISkill    {        public SerpentSupperSkill()            { super(SkillId.SERPENT_SUPPER, AnimationType.SELF);            Add(Elements.MENTAL);            AddCost(StatType.MANA, 10);        }@Override        public  void Activate(ICreature source)        {            for (int ii = 1; ii < GameManager.TileMapWidth - 1; ii++)            {                if (ii != GameManager.TileMapHeight / 2)                {                    CreatureFactory.Create(AigilasActorType.SERPENT, new Point2(ii, GameManager.TileMapHeight / 2));                }            }            for (int ii = 1; ii < GameManager.TileMapHeight - 1; ii++)            {                if (ii != GameManager.TileMapWidth / 2)                {                    CreatureFactory.Create(AigilasActorType.SERPENT, new Point2(GameManager.TileMapWidth / 2, ii));                }            }        }    }