package com.aigilas.skills.impl;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatType;import com.aigilas.entities.Elements;import com.aigilas.items.ItemFactory;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;import com.spx.core.RNG;    public class StealItemSkill  extends  ISkill    {        public StealItemSkill()            { super(SkillId.STEAL_ITEM, AnimationType.ROTATE);            Add(Elements.WATER, Elements.AIR);            AddCost(StatType.MANA, 10);        }@Override        public  void Affect(ICreature target)        {            if (RNG.Next(100) > 0)            {                _source.PickupItem(ItemFactory.CreateRandomPlain());            }        }    }