package com.aigilas.skills.impl;import com.aigilas.creatures.StatType;import com.aigilas.dungeons.DungeonFactory;import com.aigilas.entities.Elements;import com.aigilas.skills.AnimationType;import com.aigilas.skills.ISkill;import com.aigilas.skills.SkillId;import com.spx.entities.IEntity;public class WallPunchSkill extends ISkill {	public WallPunchSkill()	{		super(SkillId.WALL_PUNCH, AnimationType.RANGED);		Add(Elements.EARTH);		AddCost(StatType.MANA, 10);	}	@Override	public void Affect(IEntity target)	{		if (target.GetEntityType() == com.aigilas.EntityType.WALL) {			if (target.GetLocation().GridX > 0					&& target.GetLocation().GridX < DungeonFactory.BlocksWide - 1					&& target.GetLocation().GridY > 0					&& target.GetLocation().GridY < DungeonFactory.BlocksHigh - 1) {				target.SetInactive();			}		}	}}