package com.aigilas.states;import com.aigilas.management.Commands;import com.spx.core.XnaManager;import com.spx.io.Input;import com.spx.states.State;import com.spx.states.StateManager;import com.xna.wrapper.Color;import com.xna.wrapper.Texture2D;import com.xna.wrapper.Vector2;    public class GameOverState implements State    {        private Texture2D _menuBase;        public GameOverState()        {            _menuBase = XnaManager.GetGameOverAsset();        }        @Override		public void Draw()        {            float x = (XnaManager.WindowWidth - _menuBase.Bounds.Right)/2;            float y = (XnaManager.WindowHeight - _menuBase.Bounds.Bottom) / 2;            XnaManager.Renderer.Draw(_menuBase, new Vector2(x,y), Color.White);        }        @Override		public void Update()        {            if(Input.IsActive(Commands.Confirm,0,true))            {                StateManager.LoadState(new GameplayState());            }        }        @Override		public void LoadContent()        {        }    }