package com.aigilas.status.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.strategies.*;import com.aigilas.entities.*;import com.aigilas.skills.*;import com.spx.core.*;    public class SelfMutilationStatus  extends  IStatus    {        private int previousStrategy;        public SelfMutilationStatus(ICreature target)            { super(target);        }@Override        public  void Setup()        {            super.Setup();            previousStrategy = _target.GetStrategyId();            _target.SetStrategy(StrategyFactory.Create(Strategy.AttackSelf, _target));        }@Override        public  void Cleanup()        {            super.Cleanup();            _target.SetStrategy(StrategyFactory.Create(previousStrategy, _target));        }    }