package com.aigilas.statuses.impl;import com.aigilas.creatures.ICreature;import com.aigilas.creatures.StatBuff;import com.aigilas.creatures.StatType;import com.aigilas.statuses.IStatus;import com.spx.core.RNG;public class RandomBuffStatus extends IStatus {	public RandomBuffStatus(ICreature target)	{		super(target);		_buff = new StatBuff(StatType.Values[RNG.Next(0, 3)], 10);		Setup();	}}