package com.aigilas.statuses.impl;import com.aigilas.creatures.ICreature;import com.aigilas.statuses.IStatus;import com.aigilas.strategies.Strategy;import com.aigilas.strategies.StrategyFactory;    public class SelfMutilationStatus  extends  IStatus    {        private int previousStrategy;        public SelfMutilationStatus(ICreature target)            { super(target);        }@Override        public  void Setup()        {            super.Setup();            previousStrategy = _target.GetStrategyId();            _target.SetStrategy(StrategyFactory.Create(Strategy.AttackSelf, _target));        }@Override        public  void Cleanup()        {            super.Cleanup();            _target.SetStrategy(StrategyFactory.Create(previousStrategy, _target));        }    }