package com.aigilas.strategies;import com.aigilas.creatures.AigilasActorType;import com.aigilas.creatures.ICreature;import com.aigilas.strategies.impl.AttackSelfStrategy;import com.aigilas.strategies.impl.AttackStrategy;import com.aigilas.strategies.impl.ConfusedAndDyingStrategy;import com.aigilas.strategies.impl.ConfusedStrategy;import com.aigilas.strategies.impl.ControlledByPlayer;import com.aigilas.strategies.impl.FleeStrategy;import com.aigilas.strategies.impl.MinionCloudStrategy;import com.aigilas.strategies.impl.MinionFireStrategy;import com.aigilas.strategies.impl.MinionOneUseStrategy;import com.aigilas.strategies.impl.MinionRotateStrategy;import com.aigilas.strategies.impl.NullStrategy;import com.aigilas.strategies.impl.StraightLineRotateStrategy;import com.aigilas.strategies.impl.StraightLineStrategy;import com.aigilas.strategies.impl.TestBotStrategy;    public class StrategyFactory    {        public static IStrategy Create(int strategy,ICreature target,int... AigilasActorTypes)        {            switch (strategy)            {                case Strategy.Attack: return new AttackStrategy(target,AigilasActorType.PLAYER);                case Strategy.AttackSelf: return new AttackSelfStrategy(target);                case Strategy.Confused: return new ConfusedStrategy(target);                case Strategy.ConfusedAndDying: return new ConfusedAndDyingStrategy(target);                case Strategy.ControlledByPlayer: return new ControlledByPlayer(target);                               case Strategy.Flee: return new FleeStrategy(target,AigilasActorType.PLAYER);                case Strategy.MinionCloud: return new MinionCloudStrategy(target);                case Strategy.MinionExplode: return new MinionOneUseStrategy(target);                case Strategy.MinionFire: return new MinionFireStrategy(target);                case Strategy.MinionOneUse: return new MinionOneUseStrategy(target);                case Strategy.MinionRotate: return new MinionRotateStrategy(target);                             case Strategy.Mutiny: return new AttackStrategy(target, AigilasActorType.NONPLAYER);                case Strategy.Null: return new NullStrategy(target);                case Strategy.StraightLineRotate: return new StraightLineRotateStrategy(target);                case Strategy.StraightLine: return new StraightLineStrategy(target);                case Strategy.TestBot: return new TestBotStrategy(target);                                default: try {					throw new Exception("An undefined strategy was passed into the strategy factory: " + strategy);				} catch (Exception e) {										e.printStackTrace();				}                return null;            }        }    }