package com.aigilas.strategies.impl;import com.aigilas.creatures.ICreature;import com.aigilas.strategies.IStrategy;import com.aigilas.strategies.Strategy;import com.spx.core.RNG;public class ConfusedStrategy extends IStrategy {	public ConfusedStrategy(ICreature parent)	{		super(parent, Strategy.Confused);	}	@Override	public void Act() {		_parent.MoveIfPossible(RNG.Rand.nextInt(3) - 1, RNG.Rand.nextInt(3) - 1);	}}