package com.aigilas.strategies.impl;import com.aigilas.creatures.ICreature;import com.aigilas.strategies.IStrategy;import com.aigilas.strategies.Strategy;import com.spx.core.Point2;import com.spx.core.RNG;import com.spx.entities.CoordVerifier;    public class StraightLineRotateStrategy extends IStrategy    {        private Point2 _direction = new Point2(0, 1);        public StraightLineRotateStrategy(ICreature parent, int... targetTypes)            { super(parent,Strategy.StraightLineRotate);            for (int targetType:targetTypes)            {                _targets.AddTargetTypes(targetType);            }        }        private Point2 target = new Point2(0,0);@Override        public  void Act()        {            _parent.MoveIfPossible(_direction.PosX, _direction.PosY);            target.Reset(_direction.PosX + _parent.GetLocation().PosX, _direction.PosY + _parent.GetLocation().PosY);            if (CoordVerifier.IsBlocked(target) && _parent.IsCooledDown())            {                _direction.SetX(RNG.Rand.nextInt(3) - 1);                _direction.SetY(RNG.Rand.nextInt(3) - 1);                            }                   }    }