package com.aigilas.strategies.impl;import com.aigilas.creatures.ICreature;import com.aigilas.strategies.IStrategy;import com.aigilas.strategies.Strategy;import com.spx.core.GameManager;    class StraightLineStrategy extends IStrategy    {        public StraightLineStrategy(ICreature parent, int... targetTypes)            { super(parent,Strategy.StraightLine);            for (int targetType:targetTypes)            {                _targets.AddTargetTypes(targetType);            }        }@Override        public  void Act()        {            _parent.MoveIfPossible(0, 1);            if(_parent.GetLocation().GridY >= GameManager.TileMapHeight-1)            {                _parent.SetInactive();            }        }    }