package com.aigilas.strategies.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.spx.entities.*;import com.spx.core.*;    class StraightLineStrategy extends IStrategy    {        public StraightLineStrategy(ICreature parent, int... targetTypes)            { super(parent,Strategy.StraightLine);            for (int targetType:targetTypes)            {                _targets.AddTargetTypes(targetType);            }        }@Override        public  void Act()        {            _parent.MoveIfPossible(0, 1);            if(_parent.GetLocation().GridY >= GameManager.TileMapHeight-1)            {                _parent.SetInactive();            }        }    }