package aigilas;import org.newdawn.slick.AppGameContainer;import org.newdawn.slick.GameContainer;import org.newdawn.slick.SlickException;import org.newdawn.slick.state.StateBasedGame;import spx.io.Client;import spx.io.Server;import xna.wrapper.Console;import xna.wrapper.Environment;public class EntryPoint extends StateBasedGame {	public EntryPoint(String name) {		super(name);	}	public static void main(String[] args) {		Thread server = new Thread(new ServerThread());		server.run();		try {			Thread.sleep(100);		} catch (InterruptedException e) {			e.printStackTrace();		}		AppGameContainer app;		try {			app = new AppGameContainer(new EntryPoint("Aigilas"));			app.setDisplayMode(900, 900, false);			app.setTargetFrameRate(20);			app.setShowFPS(false);			app.start();		} catch (SlickException e) {			e.printStackTrace();		}		Server.Get().Close();		Client.Get().Close();		Console.WriteLine("Finished being a client");		Environment.Exit();	}	@Override	public void initStatesList(GameContainer arg0) throws SlickException {		addState(new Aigilas());	}}