package aigilas;import spx.io.Server;import xna.wrapper.Console;public class ServerThread implements Runnable {	@Override	public void run() {		Server.Get();		try {			Thread.sleep(50);		} catch (InterruptedException e) {			e.printStackTrace();		}		while (Server.Get().IsOnlyInstance()) {			Server.Get().Update();		}		Console.WriteLine("Finished being a server");	}}