package aigilas.gods; import com.badlogic.gdx.graphics.Texture; import java.util.ArrayList;import java.util.List;import aigilas.classes.CreatureClass;import aigilas.classes.EnvyAcolyte;import aigilas.classes.GluttonyAcolyte;import aigilas.classes.GreedAcolyte;import aigilas.classes.LustAcolyte;import aigilas.classes.NoClass;import aigilas.classes.PrideAcolyte;import aigilas.classes.SlothAcolyte;import aigilas.classes.WrathAcolyte;import aigilas.items.ItemClass;import com.badlogic.gdx.graphics.Color;public class God {	public static God Get(int name) {		for (int ii = 0; ii < __gods.size(); ii++) {			if (__gods.get(ii)._name == name) {				return __gods.get(ii);			}		}		return null;	}	private static List<God> __gods;	static {		__gods = new ArrayList<God>();		__gods.add(new God(Color.MAGENTA, GodId.LUST, ItemClass.Leggings, ItemClass.Melee_Weapon));		__gods.add(new God(Color.YELLOW, GodId.GREED, ItemClass.Head_Gear, ItemClass.Gloves));		__gods.add(new God(Color.GRAY, GodId.SLOTH, ItemClass.Shield, ItemClass.Head_Gear));		__gods.add(new God(Color.YELLOW, GodId.ENVY, ItemClass.Torso_Garb, ItemClass.Ranged_Weapon));		__gods.add(new God(Color.RED, GodId.WRATH, ItemClass.Melee_Weapon, ItemClass.Ranged_Ammo));		__gods.add(new God(Color.GREEN, GodId.GLUTTONY, ItemClass.Gloves, ItemClass.Torso_Garb));		__gods.add(new God(Color.CYAN, GodId.PRIDE, ItemClass.Ring, ItemClass.Feet));	}	private final Color _color;	private final int _name;	private final int _goodSacrificeClass;	private final int _badSacrificeClass;	public String NameText;	protected God(Color color, int name, int goodSacrifice, int badSacrifice) {		_color = color;		_name = name;		_goodSacrificeClass = goodSacrifice;		_badSacrificeClass = badSacrifice;		NameText = GodId.Names[_name];	}	public Color GetColor() {		return _color;	}	public CreatureClass GetClass() {		switch (_name) {			case GodId.ENVY:				return new EnvyAcolyte();			case GodId.GLUTTONY:				return new GluttonyAcolyte();			case GodId.GREED:				return new GreedAcolyte();			case GodId.LUST:				return new LustAcolyte();			case GodId.PRIDE:				return new PrideAcolyte();			case GodId.SLOTH:				return new SlothAcolyte();			case GodId.WRATH:				return new WrathAcolyte();		}		return new NoClass();	}	public boolean IsGoodSacrifice(int sacrifice) {		return sacrifice == _goodSacrificeClass;	}	public boolean IsBadSacrifice(int sacrifice) {		return sacrifice == _badSacrificeClass;	}}