package aigilas.hud; import com.badlogic.gdx.graphics.Texture;  import com.badlogic.gdx.graphics.Color;import java.util.ArrayList;import java.util.List;import spx.core.Point2;import spx.core.XnaManager;import spx.text.TextHandler;import aigilas.creatures.ICreature;public class IHud {	protected boolean _isVisible = false;	protected ICreature _parent;	protected static Texture _menuBase;	protected TextHandler _textHandler = new TextHandler();	protected List<Point2> playerHudPositions = new ArrayList<Point2>();	protected Point2 _dimensions;	protected IHud(ICreature owner, int width, int height) {		_parent = owner;		if (_menuBase == null) {			_menuBase = XnaManager.GetMenuBaseAsset().getScaledCopy(					(int) XnaManager.GetCenter().PosX,					(int) XnaManager.GetCenter().PosY);		}		_dimensions = new Point2(width, height);		playerHudPositions.add(new Point2(0, 0));		playerHudPositions.add(new Point2(XnaManager.WindowWidth				- _dimensions.X, 0));		playerHudPositions.add(new Point2(0, XnaManager.WindowHeight				- _dimensions.Y));		playerHudPositions.add(new Point2(XnaManager.WindowWidth				- _dimensions.X, XnaManager.WindowHeight - _dimensions.Y));	}	public void Toggle() {		_isVisible = !_isVisible;	}	public boolean IsVisible() {		return _isVisible;	}	public void LoadContent() {		_menuBase = XnaManager.GetMenuBaseAsset();	}	protected Point2 GetHudOrigin() {		return playerHudPositions.get(_parent.GetPlayerIndex());	}}