package aigilas.skills; import com.badlogic.gdx.graphics.Texture;  import com.badlogic.gdx.graphics.Color;import java.util.List;import spx.entities.Entity;import spx.entities.EntityManager;import spx.entities.IActor;import spx.entities.IEntity;import aigilas.creatures.ICreature;import aigilas.entities.Elements;import aigilas.entities.Extensions;import aigilas.entities.SkillEffect;import aigilas.management.SpriteType;import aigilas.skills.behaviors.SkillBehavior;import aigilas.statuses.StatusFactory;public abstract class ISkill {	protected ICreature _source;	protected String _implementationId;	protected SkillBehavior _behavior;	protected SkillComponents _components;	public boolean StartOffCenter = false;	protected ISkill(String implementationId, int animation, float strength,			boolean isPersistent, int effectGraphic) {		Construct(implementationId, animation, strength, isPersistent,				effectGraphic);	}	protected ISkill(String implementationId, int animation, float strength,			boolean isPersistent) {		Construct(implementationId, animation, strength, isPersistent,				SpriteType.SKILL_EFFECT);	}	protected ISkill(String implementationId, int animation, float strength) {		Construct(implementationId, animation, strength, false,				SpriteType.SKILL_EFFECT);	}	protected ISkill(String implementationId, int animation) {		Construct(implementationId, animation, SkillEffect.DefaultStrength,				false, SpriteType.SKILL_EFFECT);	}	protected void Construct(String implementationId, int animation,			float strength, boolean isPersistent, int effectGraphic) {		_implementationId = implementationId;		_components = new SkillComponents(strength, isPersistent);		_behavior = SkillFactory.Create(animation, effectGraphic, this);	}	protected void Add(int... elements) {		_components.AddElements(elements);	}	protected void AddCost(String stat, float cost) {		_behavior.AddCost(stat, cost);	}	public void SetBuff(String stat, float amount) {		_components.SetBuff(stat, amount);	}	public void Activate(ICreature source) {		_source = source;		_behavior.Activate(source);	}	public void Affect(IEntity target) {		ICreature creature = Extensions.IsCreature(target);		if (creature != null) {			Affect(creature);		}	}	public void ApplyToPlayers(int statusId) {		for (IActor player : EntityManager.GetPlayers()) {			StatusFactory.Apply((ICreature) player, statusId);		}	}	public List<Integer> GetElements() {		return _components.GetElements();	}	public void Affect(ICreature target) {	}	public int GetSpriteType() {		return _behavior.GetSpriteType();	}	public int GetAnimationType() {		return _behavior.GetAnimationType();	}	public float GetStrength() {		return _components.GetStrength();	}	public boolean IsPersistent() {		return _components.IsPersistent();	}	public Color GetElementColor() {		return Elements.Colors[_components.GetElements().get(0)];	}	public void Cleanup(Entity target, SkillEffect source) {		_behavior.Cleanup(target, source);	}	public boolean AffectTarget(ICreature target, SkillEffect graphic) {		return _behavior.AffectTarget(target, graphic);	}	public boolean IsActive() {		return _behavior.IsActive();	}	public List<Integer> GetTargetTypes() {		return _components.GetTargetTypes();	}	public String GetSkillId() {		return _implementationId;	}	public float GetCost() {		return _behavior.GetCost();	}}