package aigilas.skills;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import spx.bridge.ActorType;import spx.bridge.EntityType;import aigilas.creatures.ICreature;import aigilas.creatures.StatBuff;public class SkillComponents {	protected List<Integer> _elements;	protected StatBuff _buff;	protected float _effectStrength = 0;	protected boolean _isPersistent = false;	protected List<EntityType> _targetTypes = Arrays.asList(EntityType.WALL);	protected List<ActorType> _targetActorTypes = new ArrayList<>();	public SkillComponents(float strength, boolean isPersistent) {		_effectStrength = strength;		_isPersistent = isPersistent;		_elements = new ArrayList<Integer>();	}	public void AddElements(int... elements) {		for (int element : elements) {			_elements.add(element);		}	}	public void Buff(ICreature target) {		target.AddBuff(_buff);	}	public void SetBuff(String stat, float amount) {		_buff = new StatBuff(stat, amount);	}	public float GetStrength() {		return _effectStrength;	}	public boolean IsPersistent() {		return _isPersistent;	}	public List<Integer> GetElements() {		return _elements;	}	public List<EntityType> GetTargetTypes()	{		return _targetTypes;	}}