package aigilas.skills.behaviors; import com.badlogic.gdx.graphics.Texture;  import com.badlogic.gdx.graphics.Color;import spx.core.Point2;import aigilas.creatures.ICreature;import aigilas.skills.AnimationType;import aigilas.skills.ISkill;public class RotateBehavior extends SkillBehavior {	public RotateBehavior(int effectGraphic, ISkill parentSkill) {		super(effectGraphic, AnimationType.ROTATE, parentSkill);	}	@Override	public void Activate(ICreature target) {		_sideEffects.Generate(target.GetLocation(), new Point2(0, 0), target);	}}