package aigilas.skills.impl;import spx.core.GameManager;import spx.core.Point2;import aigilas.creatures.AigilasActorType;import aigilas.creatures.CreatureFactory;import aigilas.creatures.ICreature;import aigilas.creatures.StatType;import aigilas.entities.Elements;import aigilas.skills.AnimationType;import aigilas.skills.ISkill;import aigilas.skills.SkillId;public class SerpentSupperSkill extends ISkill {	public SerpentSupperSkill()	{		super(SkillId.SERPENT_SUPPER, AnimationType.SELF);		Add(Elements.MENTAL);		AddCost(StatType.MANA, 10);	}	@Override	public void Activate(ICreature source) {		for (int ii = 1; ii < GameManager.TileMapWidth - 1; ii++) {			if (ii != GameManager.TileMapHeight / 2) {				CreatureFactory.Create(AigilasActorType.SERPENT, new Point2(ii,						GameManager.TileMapHeight / 2));			}		}		for (int ii = 1; ii < GameManager.TileMapHeight - 1; ii++) {			if (ii != GameManager.TileMapWidth / 2) {				CreatureFactory.Create(AigilasActorType.SERPENT, new Point2(						GameManager.TileMapWidth / 2, ii));			}		}	}}