package aigilas.states;import aigilas.management.Commands;import spx.core.XnaManager;import spx.io.Input;import spx.states.State;import spx.states.StateManager;import xna.wrapper.Color;import xna.wrapper.Texture2D;import xna.wrapper.Vector2;public class GameOverState implements State {	private Texture2D _menuBase;	public GameOverState() {		_menuBase = XnaManager.GetGameOverAsset();	}	@Override	public void Draw() {		float x = (XnaManager.WindowWidth - _menuBase.Bounds.Right) / 2;		float y = (XnaManager.WindowHeight - _menuBase.Bounds.Bottom) / 2;		XnaManager.Renderer.Draw(_menuBase, new Vector2(x, y), Color.White);	}	@Override	public void Update() {		if (Input.IsActive(Commands.Confirm, 0, true)) {			StateManager.LoadState(new GameplayState());		}	}	@Override	public void LoadContent() {	}}