package aigilas.statuses.impl; import com.badlogic.gdx.graphics.Texture;  import com.badlogic.gdx.graphics.Color;import aigilas.creatures.CreatureAction;import aigilas.creatures.ICreature;import aigilas.statuses.IStatus;import aigilas.strategies.Strategy;import aigilas.strategies.StrategyFactory;public class ConfusionStatus extends IStatus {	private int previousStrategy;	public ConfusionStatus(ICreature target)	{		super(target);		_prevents.add(CreatureAction.WontHitNonTargets);	}	@Override	public void Setup() {		super.Setup();		previousStrategy = _target.GetStrategyId();		_target.SetStrategy(StrategyFactory.Create(Strategy.Confused, _target));	}	@Override	public void Cleanup() {		super.Cleanup();		_target.SetStrategy(StrategyFactory.Create(previousStrategy, _target));	}}