package aigilas.statuses.impl; import com.badlogic.gdx.graphics.Color;import spx.core.RNG;import aigilas.creatures.ICreature;import aigilas.creatures.StatBuff;import aigilas.creatures.StatType;import aigilas.statuses.IStatus;public class RandomBuffStatus extends IStatus {	public RandomBuffStatus(ICreature target)	{		super(target);		_buff = new StatBuff(StatType.Values[RNG.Next(0, 3)], 10);		Setup();	}}