package spx.devtools;import org.newdawn.slick.Color;import org.newdawn.slick.Image;import spx.core.Point2;import spx.core.XnaManager;import spx.text.TextType;public class DevConsole {	private class ConsoleText extends spx.text.Text {		public ConsoleText(int x, int y, String content) {			super(content, x, y, TextType.Action);		}		@Override		public void Draw() {			XnaManager.Renderer.DrawString(_contents, _position, Color.white,					1.0f, .9995f);		}		public String GetContent() {			return _contents;		}	}	private static DevConsole __instance;	public static DevConsole Get() {		if (__instance == null) {			__instance = new DevConsole();		}		return __instance;	}	private final ConsoleText[] _contents = new ConsoleText[10];	private int _index = 0;	private boolean _isVisible = false;	private final Color _bgColor;	private final Image _consoleBase;	private DevConsole() {		_bgColor = Color.black;		_bgColor.a = (byte) 180;		_consoleBase = XnaManager.GetMenuBaseAsset().getScaledCopy(				XnaManager.WindowWidth, XnaManager.WindowHeight);		Add("The console has been started.");	}	public void Add(String message) {		if (_index < _contents.length) {			_contents[_index++] = new ConsoleText(50, _index * 50 + 50, message);		} else {			for (int ii = 0; ii < _contents.length - 1; ii++) {				_contents[ii] = new ConsoleText(50, ii * 50 + 50,						_contents[ii + 1].GetContent());			}			_contents[_contents.length - 1] = new ConsoleText(50,					(_contents.length - 1) * 50 + 50, message);		}	}	public void Draw() {		if (_isVisible) {			XnaManager.Renderer.Draw(_consoleBase, Point2.Zero, .9993f,					_bgColor);			for (int ii = 0; ii < _contents.length; ii++) {				if (_contents[ii] != null) {					_contents[ii].Draw();				}			}		}	}	public void Toggle() {		_isVisible = !_isVisible;	}}