package spx.sprites; import com.badlogic.gdx.graphics.Texture;  import com.badlogic.gdx.graphics.Color;import spx.core.GameManager;public class SpriteInfo {	public int X, Y, SpriteIndex, MaxFrame;	public SpriteInfo(int spriteIndex, int maxFrame) {		X = GameManager.SpriteWidth;		Y = GameManager.SpriteHeight;		SpriteIndex = spriteIndex;		MaxFrame = maxFrame;	}}