package spx.sprites; import com.badlogic.gdx.graphics.Color;import java.util.HashMap;public class SpriteSheetManager {	private static HashMap<Integer, SpriteInfo> __manager = new HashMap<Integer, SpriteInfo>();	public static SpriteInfo GetSpriteInfo(int spriteName) {		return __manager.get(spriteName);	}	public static void Setup(ISpriteInitializer initializer) {		for (SpriteDefinition sprite : initializer.GetSprites()) {			__manager.put(sprite.Type, sprite.Info);		}	}}