package spx.text;import spx.core.Depth;import spx.core.GameManager;import spx.core.Point2;import spx.core.XnaManager;import com.badlogic.gdx.graphics.Color;class DefaultHudText extends Text {	private final Color _color;	private Point2 _origin;	public DefaultHudText(float alpha) {		_color = new Color(255f, 255f, 255f, alpha);	}	public void Reset(String contents, int x, int y, Point2 origin) {		_origin = origin;		Reset(contents, x, y);	}	@Override	public int Update() {		return 1;	}	@Override	public void Draw() {		XnaManager.Renderer.DrawString(_contents, new Point2(_position.X + GameManager.SpriteWidth, XnaManager.WindowHeight - _position.Y + GameManager.SpriteHeight / 2), _color, 1f, Depth.DefaultHudText);	}}